sqlMap.update:
		declare
		recordId number(10);
		hstId number(10);
		subList varray_varchar;
		subId number;
		crn VARCHAR2(200):=#crn#;
		processCycle
		number:=#processCycleId#;
		userName VARCHAR2(200):=#userName#;


		begin
		subList:= varray_varchar
		
			#ids[].subjectId#
				 
		;
		for elem in 1 .. subList.count
		loop
		subId:=subList(elem);
		dbms_output.put_line(subId);

		select RG_BACKGROUND_SUMMARY_SEQ.NEXTVAL
		into recordId from dual;



		INSERT INTO RG_BACKGROUND_SUMMARY
		(BACKGROUND_SUMMARY_ID,SUBJECT_ID,PROCESSCYCLEID,CASE_ID,
		UPDATED_ON,UPDATED_BY,TITLE,TABLE_BOTTOM_NOTES)

		values(recordId, subId
		, processCycle , crn
		,sysdate,userName,'Background Information',(select
		DEFAULT_TEXT from rg_component_default_text where component_id=15));



		end loop;

		END;
