sqlMap.update:
		declare
		recordId number(10);
		hstId number(10);
		detailHistId number(10);
		subList varray_varchar;
		compTypeArray varray_varchar;
		compTypeTitleArray varray_varchar;
		subId number;
		crn
		VARCHAR2(200):=#crn#;
		processCycle number:=#processCycleId#;
		userName
		VARCHAR2(200):=#userName#;
		compType VARCHAR2(200);
		compTypeTitle
		VARCHAR2(2000);
		fieldArray
		varray_varchar;
		fieldVal
		varchar2(200);
		detailId number(10);

		begin
		subList:= varray_varchar
		
			#ids[].subjectId#
        
		;
		compTypeArray
		:=varray_varchar('companyRegistryBGIS3DetailsForm','otherCompanyBGInfoIS3Form');
		compTypeTitleArray
		:=varray_varchar('COMPANY REGISTRY
		DETAILS','OTHER
		COMPANY INFORMATION');
		fieldArray :=varray_varchar('Name','Identifiers
		(if any)','No. of Shares / Investment (Currency)','Percentage
		(%)','Total');


		for elem in 1 .. subList.count
		loop

		subId:=subList(elem);
		for elem2 in 1 ..
		compTypeArray.count loop
		compType
		:=compTypeArray(elem2);
		compTypeTitle
		:=compTypeTitleArray(elem2);
		select
		RG_COM_BG_SUMMARY_IS3_SEQ.NEXTVAL
		into
		recordId from dual;
		if
		compType ='companyRegistryBGIS3DetailsForm'
		then
		INSERT
		INTO
		RG_COM_BG_SUMMARY_IS3
		(COM_BG_SUMMARY_IS3_ID,SUBJECT_ID,PROCESSCYCLEID,CASE_ID,
		UPDATED_ON,UPDATED_BY,IS_AUTO_SAVED,TITLE,COM_TYPE,STATUS,DESCRIPTION_OF_STATUS)
		values(recordId,
		subId ,
		processCycle, crn
		,sysdate,userName
		,'N',compTypeTitle,compType,'Available',(select DEFAULT_TEXT from rg_component_default_text where component_id=19));
		else
		INSERT
		INTO
		RG_COM_BG_SUMMARY_IS3
		(COM_BG_SUMMARY_IS3_ID,SUBJECT_ID,PROCESSCYCLEID,CASE_ID,
		UPDATED_ON,UPDATED_BY,IS_AUTO_SAVED,TITLE,COM_TYPE)
		values(recordId,
		subId ,
		processCycle, crn
		,sysdate,userName
		,'N',compTypeTitle,compType);
		end if;

		insert
		into
		RG_COM_BG_DETAILS_IS3
		(COM_BG_DETAILS_IS3_ID,COM_BG_SUMMARY_IS3_ID,ROW_NO,FIELD_NAME,COMMENTS)
		SELECT
		RG_COM_BG_DETAILS_IS3_SEQ.nextval,recordId,ROW_NO,FIELD_NAME,COMMENTS
		FROM
		RG_COM_BG_DETAILS_STATIC_IS3 where SUB_COMPONENT_TYPE=compType;

		END
		LOOP;
		END LOOP;
		END;

sqlMap.insert:
		


		declare

		recordId number;
		rowNoList varray_varchar;
		fieldNameList
		varray_varchar;
		fieldValueList varray_varchar;
		commentValList
		varray_varchar;
		detailIdList varray_varchar;


		rowNoId number(10);
		fieldName VARCHAR2(4000);
		fieldValue VARCHAR2(4000);
		commentVal
		VARCHAR2(4000);
		parentId number:=#parentId#;
		detailId
		number(10);


		begin
		rowNoList:= varray_varchar
		
			#ids[].rowNo#
				 
		;
		fieldNameList:= varray_varchar
		
			#ids[].fieldName#
				 
		;
		fieldValueList:= varray_varchar
		
			#ids[].fieldValue#
				 
		;

		commentValList:= varray_varchar
		
			#ids[].comment#
				 
		;

		detailIdList:= varray_varchar
		
			#ids[].id#
				 
		;

		for elem in 1 .. rowNoList.count
		loop
		rowNoId:=rowNoList(elem);
		fieldName:=fieldNameList(elem);
		fieldValue:=fieldValueList(elem);
		commentVal:=commentValList(elem);
		detailId := detailIdList(elem);

		IF
		detailId = 0 THEN

		select RG_COM_BG_DETAILS_IS3_SEQ.NEXTVAL
		into recordId
		from dual;

		insert into
		RG_COM_BG_DETAILS_IS3 (
		COM_BG_DETAILS_IS3_ID,
		ROW_NO,
		FIELD_NAME,
		FIELD_VALUE,
		COMMENTS,
		COM_BG_SUMMARY_IS3_ID
		)

		values(recordId, rowNoId ,
		fieldName , fieldValue, commentVal,
		parentId);

		ELSE

		update
		RG_COM_BG_DETAILS_IS3 set
		ROW_NO=rowNoId,
		FIELD_NAME
		=fieldName,
		FIELD_VALUE =fieldValue
		where
		COM_BG_DETAILS_IS3_ID =
		detailId;

		END IF;
		end loop;
		commit;
		END;

sqlMap.select:
SELECT * FROM (select subpc.*, ROWNUM rn from ( select distinct rg.subject_id, rg.processcycleid, rg.case_id, sub.report_order,cc.processcycle_order,rg.IS_AUTO_SAVED,sub.is_primary_sub ,sub.REPORT_SUBJECT_NAME from RG_COM_BG_SUMMARY_IS3 rg join RG_SUBJECT sub on sub.subject_id = rg.subject_id join cms_casecycle cc on cc.casecycle_id = rg.processcycleid group by rg.subject_id, rg.processcycleid, rg.case_id, sub.REPORT_ORDER, cc.processcycle_order,rg.IS_AUTO_SAVED,sub.is_primary_sub ,sub.REPORT_SUBJECT_NAME having rg.case_id = #crn# and upper(rg.IS_AUTO_SAVED)=upper('N')
order by sub.is_primary_sub desc,sub.REPORT_ORDER asc,upper(sub.REPORT_SUBJECT_NAME) asc,cc.processcycle_order desc
) subpc WHERE ROWNUM <= #limit#) WHERE rn >= #start#
