sqlMap.update:
		declare
		recordId number(10);
		hstId number(10);
		detailId number(10);
		detailHistId number(10);

		subList varray_varchar;
		subId number;
		crn
		VARCHAR2(200):=#crn#;
		processCycle
		number:=#processCycleId#;
		teamId
		number:=#teamId#;
		userName
		VARCHAR2(200):=#userName#;
		fieldArray
		varray_varchar;
		fieldVal
		varchar2(200);

		begin
		subList:= varray_varchar
		
			#ids[].subjectId#
				 
		;
		fieldArray :=varray_varchar('<p>Name</p>','<p>No. of Shares / Investment (Currency)</p>','<p>Percentage (%)</p>','Total');
		for elem in 1
		.. subList.count

		loop
		subId:=subList(elem);
		select
		RG_COM_BACKGROUND_SUMMARY_SEQ.NEXTVAL
		into
		recordId from dual;

		INSERT INTO
		RG_COM_BACKGROUND_SUMMARY
		(COM_BACKGROUND_SUMMARY_ID,SUBJECT_ID,PROCESSCYCLEID,CASE_ID,
		UPDATED_ON,UPDATED_BY,IS_AUTO_SAVED,TITLE,COM_TYPE)
		values(recordId,
		subId , processCycle, crn
		,sysdate,userName
		,'N','Shareholders (as on / latest available information)','shareholdersForm');

		for rown in 1..4 Loop

		if rown = 1 then

		for
		coln in 1..3 Loop
		select
		RG_SHAREHOLDER_DETAILS_SEQ.nextval into
		detailId from dual;

		insert into RG_SHAREHOLDER_DETAILS
		(SHAREHOLDER_DETAILS_ID,COM_BACKGROUND_SUMMARY_ID,ROW_NO,COLUMN_NO,FIELD_VALUE)
		values(detailId,recordId,1,coln-1,fieldArray(coln));

		END LOOP;

		else
		if
		rown = 4 then
		for
		coln in 1..3 Loop
		if coln = 1 then
		select
		RG_SHAREHOLDER_DETAILS_SEQ.nextval into
		detailId from
		dual;
		insert into
		RG_SHAREHOLDER_DETAILS
		(SHAREHOLDER_DETAILS_ID,COM_BACKGROUND_SUMMARY_ID,ROW_NO,COLUMN_NO,FIELD_VALUE)
		values(detailId,recordId,rown,coln-1,fieldArray(4));
		else
		select
		RG_SHAREHOLDER_DETAILS_SEQ.nextval into
		detailId from
		dual;
		insert into
		RG_SHAREHOLDER_DETAILS
		(SHAREHOLDER_DETAILS_ID,COM_BACKGROUND_SUMMARY_ID,ROW_NO,COLUMN_NO)
		values(detailId,recordId,rown,coln-1);
		end if;
		END LOOP;
		else
		for
		coln in
		1..3 Loop
		DBMS_OUTPUT.PUT_LINE('row is
		:'||rown);
		select
		RG_SHAREHOLDER_DETAILS_SEQ.nextval into
		detailId from
		dual;


		insert into
		RG_SHAREHOLDER_DETAILS
		(SHAREHOLDER_DETAILS_ID,COM_BACKGROUND_SUMMARY_ID,ROW_NO,COLUMN_NO)
		values(detailId,recordId,rown,coln-1);

		END LOOP;
		end if;
		end if;
		END
		LOOP;
		END LOOP;
		END;

		declare

		recordId number;
		rowNoList varray_varchar;
		columnNoList
		varray_varchar;
		fieldValueList varray_varchar;
		detailIdList
		varray_varchar;


		rowNoId number(10);
		columnNoId number(10);
		fieldValue VARCHAR2(4000);
		parentId
		number:=#parentId#;
		detailId number(10);


		begin
		rowNoList:= varray_varchar
		
			#ids[].rowNo#
				 
		;
		columnNoList:= varray_varchar
		
			#ids[].columnNo#
				 
		;
		fieldValueList:= varray_varchar
		
			#ids[].fieldValue#
				 
		;

		detailIdList:= varray_varchar
		
			#ids[].id#
				 
		;



		for elem in 1 .. rowNoList.count
		loop
		rowNoId:=rowNoList(elem);
		columnNoId:=columnNoList(elem);
		fieldValue:=fieldValueList(elem);
		detailId := detailIdList(elem);

		IF detailId = 0 THEN

		select RG_SHAREHOLDER_DETAILS_SEQ.NEXTVAL
		into recordId from dual;

		insert into
		RG_SHAREHOLDER_DETAILS (
		SHAREHOLDER_DETAILS_ID,
		ROW_NO,
		COLUMN_NO,
		FIELD_VALUE,
		COM_BACKGROUND_SUMMARY_ID
		)

		values(recordId, rowNoId ,
		columnNoId , fieldValue, parentId);


		ELSE

		update
		RG_SHAREHOLDER_DETAILS set
		ROW_NO=rowNoId,
		COLUMN_NO=columnNoId,
		FIELD_VALUE=fieldValue
		where
		SHAREHOLDER_DETAILS_ID = detailId;
		END IF;

		end loop;

		commit;

		END;
