sqlMap.update:
		declare
		recordId number(10);
		hstId number(10);
		detailId number(10);
		detailHistId number(10);

		subList varray_varchar;
		subId number;
		crn
		VARCHAR2(200):=#crn#;
		processCycle
		number:=#processCycleId#;
		teamId
		number:=#teamId#;
		userName
		VARCHAR2(200):=#userName#;
		fieldArray
		varray_varchar;
		fieldVal
		varchar2(200);

		begin
		subList:= varray_varchar
		
			#ids[].subjectId#
				 
		;
		fieldArray :=varray_varchar('Name','No. of Shares / Investment (Currency)','Percentage (%)','Total');
		for elem in 1
		.. subList.count

		loop
		subId:=subList(elem);
		select
		RG_CMPY_CORP_ANA_SUMRY_SEQ.NEXTVAL into
		recordId from dual;
	
		INSERT INTO
		RG_CMPY_CORP_ANA_SUMRY
		(CMPY_CORP_ANA_SUMRY_ID,SUBJECT_ID,PROCESSCYCLEID,CASE_ID,
		UPDATED_ON,UPDATED_BY,IS_AUTO_SAVED,TITLE)
		values(recordId, subId , processCycle, crn
		,sysdate,userName
		,'N','Shareholders (as
		on / latest available information / since DD Month YYYY with tenure of
		XX years)');
		
		for rown in 1..4 Loop

		if rown = 1 then		

		for
		coln in 1..3 Loop
		select
		RG_CMPY_CORP_SHARE_DTL_SEQ.nextval into
		detailId from dual;
		
		insert into RG_CMPY_CORP_SHARE_DTL 
		(CMPY_CORP_SHARE_DTL_ID,CMPY_CORP_ANA_SUMRY_ID,ROW_NO,COLUMN_NO,FIELD_VALUE)
		values(detailId,recordId,1,coln-1,fieldArray(coln));
		
		END LOOP;

		else
			if rown = 4 then
				for
				coln in 1..3 Loop
				if coln = 1 then
					select RG_CMPY_CORP_SHARE_DTL_SEQ.nextval into
					detailId from
					dual;
					insert into RG_CMPY_CORP_SHARE_DTL
					(CMPY_CORP_SHARE_DTL_ID,CMPY_CORP_ANA_SUMRY_ID,ROW_NO,COLUMN_NO,FIELD_VALUE)
					values(detailId,recordId,rown,coln-1,fieldArray(4));
				else
					select RG_CMPY_CORP_SHARE_DTL_SEQ.nextval into
					detailId from
					dual;
					insert into RG_CMPY_CORP_SHARE_DTL
					(CMPY_CORP_SHARE_DTL_ID,CMPY_CORP_ANA_SUMRY_ID,ROW_NO,COLUMN_NO)
					values(detailId,recordId,rown,coln-1);
				end if;
				END LOOP;
			else
				for
				coln in 1..3 Loop
				DBMS_OUTPUT.PUT_LINE('row is
				:'||rown);
				select RG_CMPY_CORP_SHARE_DTL_SEQ.nextval into
				detailId from
				dual;
				
		
				insert into RG_CMPY_CORP_SHARE_DTL
				(CMPY_CORP_SHARE_DTL_ID,CMPY_CORP_ANA_SUMRY_ID,ROW_NO,COLUMN_NO)
				values(detailId,recordId,rown,coln-1);
				
				END LOOP;
			end if;
		end if;
		END
		LOOP;
		END LOOP;
		END;
